mapper.insert:
INSERT INTO feed_comment
SET feed_id = #{feedId}
, user_id = #{userId}
, comment = #{comment}

mapper.select:
SELECT T.feedId, T.feedCommentId, T.comment, T.writerUserId, T.writerNm, T.writerPic FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY A.feed_id ORDER BY A.feed_id, A.feed_comment_id DESC) AS num , A.feed_id AS feedId , A.feed_comment_id AS feedCommentId , A.comment , B.user_id AS writerUserId , IF(B.nick_name IS NULL, B.uid, B.nick_name) AS writerNm , B.pic AS writerPic FROM feed_comment A INNER JOIN user B ON A.user_id = B.user_id WHERE A.feed_id IN
#{feedId}
) T WHERE T.num <= 4

mapper.delete:
DELETE FROM feed_comment
WHERE feed_comment_id = #{feedCommentId}
AND user_id = #{userId}
