mapper.insert:
INSERT INTO feed_comment
SET feed_id = #{feedId}
, user_id = #{userId}
, comment = #{comment}

mapper.select:
SELECT T1.feed_id AS feedId , T1.feed_comment_id AS feedCommentId , T1.comment , T2.user_id AS writerUserId , IF(T2.nick_name IS NULL, T2.uid, T2.nick_name) AS writerNm , T2.pic AS writerPic FROM feed_comment T1 INNER JOIN user T2 ON T1.user_id = T2.user_id WHERE T1.feed_id IN (
#{item}
) AND ( SELECT COUNT(feed_comment_id) FROM feed_comment A WHERE T1.feed_id = A.feed_id AND T1.feed_comment_id < A.feed_comment_id ) < 4 ORDER BY T1.feed_id, T1.feed_comment_id DESC

mapper.delete:
DELETE FROM feed_comment
WHERE feed_comment_id = #{feedCommentId}
AND user_id = #{userId}
