mapper.insert:
INSERT INTO feed
SET writer_user_id = #{writerUserId}
, contents = #{contents}
, location = #{location}

mapper.select:
SELECT A.feed_id AS feedId, A.contents, A.location, A.created_at AS createdAt, B.user_id AS writerUserId , IF(B.nick_name IS NULL, B.uid, B.nick_name) AS writerNm , B.uid, B.pic AS writerPic , IF(C.user_id IS NULL, 0, 1) AS isLike FROM feed A INNER JOIN user B ON A.writer_user_id = B.user_id LEFT JOIN feed_like C ON A.feed_id = C.feed_id AND C.user_id = #{signedUserId}
WHERE writer_user_id = #{profileUserId}
ORDER BY A.feed_id DESC LIMIT #{startIdx}, #{size}

mapper.delete:
DELETE FROM feed
WHERE feed_id = #{feedId}
AND writer_user_id = #{signedUserId}
